class RollbackImporter:    def __init__(self):        "Creates an instance and installs as the global importer"        self.previousModules = sys.modules.copy()            def uninstall(self):        newmodules = sys.modules.copy()        for modname in newmodules.keys():            if modname not in self.previousModules:                # Force reload when modname next imported                del(sys.modules[modname])if __name__ == "__main__":    import sys    #print(sys.path)    if "/py" not in sys.path:        sys.path.insert(0, "/py")    if "." not in sys.path:        sys.path.insert(0, ".")    rollback = RollbackImporter()        try:        from ripshell import RIPServer, config                print("Starting RIPShell... \n")        print("   - Use ripshell.py [robot ip] to access the shell\n")        print("   - If you want to disable ripshell, then rename /py/ripshell.py\n")        print("     to something else and delete ripshell.pyc\n")                RIPServer( config )    finally:            try:            from robot import run            print("Running user code.")            run()        finally:            rollback.uninstall()